static const long long INFL = 0x3f3f3f3f3f3f3f3fLL;

void solve() {
        long long n, e, t;
        cin >> n >> e >> t;
        vector<long long> x(n);
        for (int i = 0; i < n; i ++) cin >> x[i];
        vector<long long> dp(n, INFL);
        dp[0] = t;
        int now = 0;
        long long mi = INFL;
        for (int i = 1; i < n; i ++) {
                if (2 * (x[i] - x[0]) <= t) {
                        dp[i] = t;
                        continue;
                }
                mi = min(mi, -2 * x[0]);
                while (2 * (x[i] - x[now + 1]) > t) {
                        mi = min(mi, dp[now] - 2 * x[now + 1]);
                        now ++;
                }
                dp[i] = mi + 2 * x[i];
                dp[i] = min(dp[i], dp[now] + t);
        }
        cout << dp[n - 1] + e << endl;
        return;
}

