const int N = 1e5 + 10;
int n, m, k, ans, a[N], f[N];

int move(int i, int j) {
	if (j < 0 || j >= i) return f[N - 1];
	return f[j] + a[i] - a[j] + std::max((a[i] - a[j + 1]) << 1, k);
}

void main() {
	memset(f, 0x3f, sizeof(f));
	read(n), read(m), read(k);
	for (int i = 1; i <= n; i++) read(a[i]);
	f[0] = 0;
	for (int i = 1; i <= n; i++) {
		int j = std::lower_bound(a, a + i, (a[i] * 2 - k + 1) >> 1) - a;
		// printf("i=%d j=%d\n", i, j);
		f[i] = std::min(f[i], move(i, j - 2));
		f[i] = std::min(f[i], move(i, j - 1));
		f[i] = std::min(f[i], move(i, j));
		f[i] = std::min(f[i], move(i, j + 1));
		f[i] = std::min(f[i], move(i, j + 2));
	}
	// for (int i = 1; i <= n; i++) print(f[i], " \n"[i == n]);
	ans = f[n] + m - a[n];
