long long dpm2; // min dp[i] + x[i] + 2 * x[i - 1]

long long val1(int k) {
    return dp[k] + x[k];
}

long long val2(int k) {
    return dp[k] + x[k] + 2 * x[k - 1];
}

int main() {
    scanf("%d%lld%lld", &N, &E, &T);
    for (int i = 0; i < N; i++)
        scanf("%lld", &x[i]);

    dp[N - 1] = T;
    int k = N - 1;
    dpm1.insert({val1(k), k});
    dpm2 = T + 3 * E;
    for (int i = N - 2; i >= 0; i--) {
        dp[i] = x[N - 1] - x[i] + max(2 * (x[N - 1] - x[i]), T);
        /*
        for (int j = i; j + 1 < N; j++) {
            long long cost = (x[j + 1] - x[i]) + max(2 * (x[j] - x[i]), T);
            dp[i] = min(dp[i], dp[j + 1] + cost);
        }
        */
        // keep dpm1 for j <= k where 2 * (x[j] - x[i]) <= T
        // keep dpm2 for j >= k where 2 * (x[j] - x[i]) > T
        while (k > i && 2 * (x[k - 1] - x[i]) > T) {
