ll dp(ll x, ll y)
{
	if (x == n) return 0;
	if (seen[x][y]) return cache[x][y];
	seen[x][y] = true;

	ll ans1 = -1, ans2 = -1;
	if (x + 1 < n)
		ans1 = p[x+1] - p[x] + dp(x+1, y);
