	X[N] = E;
	dp[N] = 0;
	seg.update(N, dp[N] + X[N] + 2*X[N-1]);
	for (int i = N-1; i >= 0; i--) {
		// ã¾ãš T ãŒæ”¯é…çš„ãªéƒ¨åˆ†ã‚’æŽ¢ç´¢
		int low = i, high = N;
		while (high - low > 1) {
			const int med = (low + high) / 2;
			if (2*(X[med]-X[i]) <= T) {
				low = med;
			} else {
				high = med;
			}
		}
		//cout << i << " " << low << endl;
		dp[i] = dp[low+1] + X[low+1] - X[i] + T;
		dp[i] = min(dp[i], seg.query(low+1, N) - 3ll*X[i]);
