int pp;
deque<int> far, close;

ll cost(ll l, ll r) {
    return dp[l] + (x[r] - x[l]) + max(T, 2 * (x[r] - x[l + 1]));
}

void add(deque<int>& Q, int pos, int refe) {
    while (!Q.empty()) {
        int last = Q.back();
        if (cost(last, refe) < cost(pos, refe)) break;
        Q.pop_back();
    }

    Q.push_back(pos);
}

int main()
{
   // freopen("test.in","r",stdin);

    scanf("%d%lld%lld", &n, &E, &T);
