    add(close, 0, 1);

    for (i = 1; i <= n; i++) {
        while (pp + 1 < i && T < 2LL * (x[i] - x[pp + 2])) {
            pp++;
            add(far, pp, i);
        }

        while (!close.empty()) {
            if (close.front() > pp) break;
            close.pop_front();
        }

        ll d1 = inf;
        if (!far.empty()) d1 = cost(far.front(), i);

        ll d2 = inf;
        if (!close.empty()) d2 = cost(close.front(), i);

        dp[i] = min(d1, d2);
        add(close, i, i + 1);
    }

