      w = min(w, dp[j] + 2*(x[i] - x[j]));
      j++;
    }

    dp[i+1] = min(dp[i+1], w);
    if(j<=i) dp[i+1] = min(dp[i+1], dp[j] + max(2*(x[i]-x[j]) ,t));

    w += 2*(x[i+1] - x[i]);
  }
  printf("%lld\n", dp[n] + e);

  return 0;
}

