  int dT = max(0ll, T - 2ll * d);
  if (i == n) return t;
  int &ret = dp[i];
  if (ret != -1) return ret;
  ret = min(t + d + dfs(i + 1, now + 1, T), dt + 3 * d + dfs(i + 1, now + 1, dT));

  return ret;
}

