		mn[id(l,r)]=inf;
		if(l==r)return ;
		int mid=(l+r)>>1;
		build(l,mid);
		build(mid+1,r);
	}
	LL Q(int l,int r,int l0,int r0){
		if(l0<=l&&r0>=r)
