#include <bits/stdc++.h>
using namespace std;
const int N = 1e5 + 5;
int d[N], T;
long long dp[N];
long long calc_big(int j) {
	return T - d[j] + dp[j];
}
long long calc_small(int j) {
	return 2LL * (-d[j + 1]) - d[j] + dp[j];
}
struct cmp_big {
	bool operator () (const int &i, const int &j) {
		return calc_big(i) > calc_big(j);
	}
};
struct cmp_small {
	bool operator () (const int &i, const int &j) {
		return calc_small(i) > calc_small(j);
	}
};
int main() {
	int n, E;
	scanf("%d%d%d", &n, &E, &T);
	for (int i = 1; i <= n; ++ i) scanf("%d", &d[i]);
	priority_queue<int, vector<int>, cmp_big> q1;
	priority_queue<int, vector<int>, cmp_small> q2;
	q1.push(0);
	for (int i = 1; i <= n; ++ i) {
		while (!q1.empty() && 2LL * (d[i] - d[q1.top() + 1]) >= T) {
			q2.push(q1.top());
			q1.pop();
		}
		dp[i] = 1e18;
		if (!q1.empty()) {
			int j = q1.top();
			dp[i] = min(dp[i], d[i] + calc_big(j));
		}
		if (!q2.empty()) {
			int j = q2.top();
			dp[i] = min(dp[i], 3LL * d[i] + calc_small(j));
		}
		q1.push(i);
	}
	cout << dp[n] + E - d[n] << endl;
}

