const int maxn = 1e5 + 100;
int n;
int t, e;
int x[maxn];

ll st[2][maxn * 4];

void build(int t, int v, int vl, int vr) {
  st[t][v] = 1e18;
  if (vl == vr - 1) {
    return;
  }
  int mid = (vl + vr) / 2;
  build(t, v * 2, vl, mid);
  build(t, v * 2 + 1, mid, vr);
}

void update(int t, int v, int vl, int vr, int pos, ll val) {
  if (vl == vr - 1) {
    st[t][v] = val;
    return;
  }
  int mid = (vl + vr) / 2;
  if (pos < mid) {
    update(t, v * 2, vl, mid, pos, val);
  } else {
    update(t, v * 2 + 1, mid, vr, pos, val);
  }
  st[t][v] = min(st[t][v * 2], st[t][v * 2 + 1]);
}

ll get(int t, int v, int vl, int vr, int l, int r) {
  if (vr <= l || r <= vl) {
    return 1e18;
  }
  if (l <= vl && vr <= r) {
    return st[t][v];
  } else {
    int mid = (vl + vr) / 2;
    return min(get(t, v * 2, vl, mid, l, r), get(t, v * 2 + 1, mid, vr, l, r));
  }
}

bool read() {
  if (scanf("%d%d%d", &n, &e, &t) < 3) {
    return false;
  }
  x[0] = 0;
  for (int i = 1; i <= n; i++) {
    scanf("%d", x + i);
  }
  x[n + 1] = e;
  n += 2;
  return true;
}

ll dp[maxn];

void solve() {
  build(0, 1, 0, n);
  build(1, 1, 0, n);
  update(0, 1, 0, n, 0, -2 * x[1]);
  update(1, 1, 0, n, 0, 0);
  for (int i = 1; i < n; i++) {
    int left = (int) (upper_bound(x, x + n, x[i] - (t + 1) / 2) - x);
    left = max(0, left - 1);
    dp[i] = 1e18;
    if (left) {
      dp[i] = min(dp[i], get(0, 1, 0, n, 0, left) + 2 * x[i]);
    }
    if (left < i) {
      dp[i] = min(dp[i], get(1, 1, 0, n, left, i) + t);
    }
    update(0, 1, 0, n, i, dp[i] - 2 * x[i + 1]);
    update(1, 1, 0, n, i, dp[i]);
  }
  printf("%lld\n", dp[n - 2] + e);
}

