	long long tr[maxn << 2];
	inline void Upd(int o, int l, int r, int p, long long d) {
		if(l == r)
			return tr[o] = d, void();
		int mid = (l + r) >> 1;
		if(p <= mid)
			Upd(o << 1, l, mid, p, d);
		else
			Upd(o << 1 | 1, mid + 1, r, p, d);
		tr[o] = min(tr[o << 1], tr[o << 1 | 1]);
	}
	inline long long Ask(int o, int l, int r, int ql, int qr) {
		if(ql > qr)
			return 1e18;
		if(ql <= l && r <= qr)
			return tr[o];
		int mid = (l + r) >> 1;
		long long ans = 1e18;
		if(ql <= mid)
			ans = min(ans, Ask(o << 1, l, mid, ql, qr));
		if(qr > mid)
			ans = min(ans, Ask(o << 1 | 1, mid + 1, r, ql, qr));
		return ans;
	}
}g, h;
