#include <bits/stdc++.h>

using namespace std;
typedef long long ll;
const int N = 100010;
const ll INFLL = 1e18;
int _w;

int n, e, t, x[N];

namespace SGT {
	ll val[N*4], tag[N*4], ql, qr, qv;

	void _query( int o, int L, int R, ll adv ) {
		if( L >= ql && R <= qr ) {
			qv = min(qv, val[o] + adv);
		} else {
			int M = (L+R)/2, lc = o<<1, rc = lc|1;
			adv += tag[o];
			if( ql <= M ) _query(lc, L, M, adv);
			if( qr > M ) _query(rc, M+1, R, adv);
		}
	}
	ll query( int l, int r ) {
		ql = l, qr = r, qv = INFLL;
		_query(1, 1, n+1, 0);
		return qv;
	}
	void _add( int o, int L, int R ) {
		if( L >= ql && R <= qr ) {
			tag[o] += qv;
			val[o] += qv;
		} else {
			int M = (L+R)/2, lc = o<<1, rc = lc|1;
			if( ql <= M ) _add(lc, L, M);
			if( qr > M ) _add(rc, M+1, R);
			val[o] = min(val[lc], val[rc]) + tag[o];
		}
	}
	void add( int l, int r, ll v ) {
		ql = l, qr = r, qv = v;
		_add(1, 1, n+1);
	}
}

ll f[N];
void solve() {
	f[1] = x[1];
	SGT::add(1, 1, f[1]);
	int p = 1;
	for( int i = 2; i <= n+1; ++i ) {
		f[i] = x[i] - x[i-1];
		SGT::add(i-1, i-1, t);
		if( i > 2 )
			SGT::add(1, i-2, x[i-1] - x[i-2]);
		if( p > 1 )
			SGT::add(1, p-1, x[i-1] - x[i-2] + x[i-1] - x[i-2]);
		while( x[i-1] - x[p] + x[i-1] - x[p] > t ) {
			SGT::add(p, p, -t);
			SGT::add(p, p, x[i-1] - x[p] + x[i-1] - x[p]);
			++p;
		}
		f[i] += SGT::query(1, i-1);
		SGT::add(i, i, f[i]);
	}
}

int main() {
	cin >> n >> e >> t;
	for( int i = 1; i <= n; ++i )
		cin >> x[i];
	x[n+1] = e;
	solve();
	cout << f[n+1] << endl;
	return 0;
}

