struct Queue
{
	int q[MAXN],hd,tl;
	Queue(void){ hd=tl=0;}
	inline bool empty(void){ return hd>=tl;}
	inline int front(void){ return q[hd+1];}
	inline int back(void){ return q[tl];}
	inline void push(int x){ q[++tl]=x;}
	inline void pop_front(void){ ++hd;}
	inline void pop_back(void){ --tl;}
}q;

