	{
		while(!q.empty() && (a[i]-a[q.front()+1])*2 > t) q.pop_front();

		dp[i]=dp[i-1]+t;
		chk_min(dp[i], max(t, (a[i]-a[1])*2));

		if(!q.empty())
			chk_min(dp[i], dp[q.front()]+t),
			chk_min(dp[i], preMn[q.front()-1]+2*a[i]);
		else chk_min(dp[i], preMn[i-1]+2*a[i]);

