struct SegmentTree
{
    using T = int;

    int N;
    vector<T> dat;

    T id = INF;
    T F(T a, T b) { return min(a, b); }

    SegmentTree(int n){
        N = 1;
        while(n > N) N = N << 1;
        dat = vector<T>(2 * N - 1, id);
    }

    SegmentTree(int n, vector<T> v){
        N = 1;
        while(n > N) N = N << 1;
        dat = vector<T>(2 * N - 1, id);
        for(int i = 0; i < n; i++) dat[i + N - 1] = v[i];
        for(int i = N - 2; i >= 0; i--) dat[i] = F(dat[i * 2 + 1], dat[i * 2 + 2]); 
    }


    void update(int k, T a){
        k += N - 1;
        dat[k] = a;
        while(k > 0){
            k = (k - 1) / 2;
            dat[k] = F(dat[k * 2 + 1], dat[k * 2 + 2]);
        }
    }

    void reset() { fill(dat.begin(), dat.end(), id); }

    T get(int a, int b, int k, int l, int r){
        if(r <= a || b <= l) return id;
        if(a <= l && r <= b) return dat[k];
        else{
            T vl = get(a, b, k * 2 + 1, l, (l + r) / 2);
            T vr = get(a, b, k * 2 + 2, (l + r) / 2, r);
            return F(vl, vr);
        }
    }
    T get(int a, int b) { return get(a, b, 0, 0, N); }

    T val(int k){ return dat[k + N - 1]; }
};

signed main(){

    int N, E, T; cin >> N >> E >> T;
    vec x(N + 1);
    x[0] = 0; REP(i, N) cin >> x[i + 1];

    SegmentTree dp(N + 1);
    dp.update(0, -2 * x[1]);
    int ans;
    FOR(i, 1, N + 1){
        int i0 = Lower_bound(x, x[i] - T / 2);
        i0 = max(0LL, i0 - 1);
        int v1 = dp.val(i0) + 2 * x[i0 + 1] + T;
        int v2 = dp.get(0, i0) + 2 * x[i];
        if(i < N) dp.update(i, min(v1, v2) - 2 * x[i + 1]);
        else ans = min(v1, v2) + E;
    }
    Out(ans);

    return 0;
}

