const int N = 1e5 + 10;
const ll oo = 1e18 + 10;
struct Tree {
    vl t;
    Tree() {
        t.resize(N << 2, oo);
    }

    void upd(int v, int tl, int tr, int pos, ll val) {
        if (tl == tr) {
            t[v] = val;
            return;
        }
        int tm = tl + tr >> 1;
        if (pos <= tm)
            upd(v << 1, tl, tm, pos, val);
        else
            upd(v << 1 | 1, tm + 1, tr, pos, val);
        t[v] = min(t[v << 1], t[v << 1 | 1]);
    }

    ll get(int v, int tl, int tr, int l, int r) {
        if (l > r)
            return oo;
        if (tl == l && tr == r)
            return t[v];
        int tm = tl + tr >> 1;
        return min(
                get(v << 1, tl, tm, l, min(tm, r)),
                get(v << 1 | 1, tm + 1, tr, max(tm  + 1, l), r)
                );
    }
};

