    t1.upd(1, 0, n, 0, -x[0] * 3 + dp[0]);
    for (int i = 0; i < n - 1; ++i) {
        int l = -1, r = i;
        while (r - l > 1) {
            int m = r + l >> 1;
            if (2 * (x[i] - x[m]) <= T) {
                r = m;
            } else {
                l = m;
            }
        }
        ll cur2 = t2.get(1, 0, n, r, i) + T + x[i];
        ll cur1 = t1.get(1, 0, n, 0, r - 1) + x[i] * 3;
        dp[i + 1] = min(cur1, cur2) + x[i + 1] - x[i];
        t2.upd(1, 0, n, i + 1, -x[i + 1] + dp[i + 1]);
        t1.upd(1, 0, n, i + 1, -x[i + 1] * 3 + dp[i + 1]);
    }

    fori (i, n) {
