				s[1].insert(mp(nxt, cur));
			}
			cur++;
		}
		dp[i] = a[i] - (i != 0 ? a[i-1] : 0) + (i != 0 ? dp[i-1] : 0) + T;
//		if (i == 1) cout << dp[1] << endl;
		if (!s[0].empty()) {
			int now = s[0].begin()->FI;
			dp[i] = min(dp[i], a[i] + now);
		}
//		if (i == 2) cout << dp[i] << endl;
		if (!s[1].empty()) {
			int now = s[1].begin()->FI;
			dp[i] = min(dp[i], 3 * a[i] + now);			
		}
		int nxt = T - (i != 0 ? a[i - 1] : 0) + (i != 0 ? dp[i - 1] : 0);
		s[0].insert(mp(nxt, i));
	}
//	cout << dp[1] << endl;
	cout << E - a[N - 1] + dp[N - 1];


}

