long long dp[maxn];
long long dpup[maxn];

void solve() {
  sort(a, a + n);

  int pos = 0;

  vector<pair<long long, int> > st;
  int left = 0;

  for (int i = 0; i < n; ++i) {
    dpup[i] = 1e18;
  }

  for (int i = 0; i < n; ++i) {
    long long &cur = dp[i];
    if (!i) {
      cur = 0;
    } else {
      cur = 1e18;
      cur = min(cur, dpup[i] + 2 * a[i]);
      while (left < sz(st) && st[left].second <= i) {
        ++left;
      }
      if (left < sz(st)) {
        cur = min(cur, st[left].first);
      }
    }
    //eprintf("dp[%d] = %lld\n", i, cur);

    if (i < n - 1) {
      while (pos < n && (a[pos] - a[i + 1]) * 2 <= T) {
        ++pos;
      }
      long long nval = cur + T;
      while (sz(st) > 0 && st.back().first >= nval) {
        st.pop_back();
        if (left == sz(st)) {
          --left;
        }
      }
      st.push_back(mp(nval, pos));
      //eprintf("nval = %lld, pos = %d\n", nval, pos);

      dpup[pos] = min(dpup[pos], cur - 2 * a[i + 1]);
      dpup[i + 1] = min(dpup[i + 1], dpup[i]);
    }
  }

  long long res = dp[n - 1] + e;
  printf("%lld\n", res);
}

