int n,e,T;
ll dp[100010];
int dis[100010];
ll inf=1LL<<60;

struct node{
    int s,e,m;
    ll mini=0;
    node *l,*r;
    node(int a,int b){
        s=a;e=b;m=(a+b)/2;
        if(s==e)return;
        l=new node(s,m);
        r=new node(m+1,e);
    }
    void update(int pos,int val){
        if(s==e){
            mini=val;
            return;
        }
        if(pos<=m)l->update(pos,val);
        else r->update(pos,val);
        mini=min(l->mini,r->mini);
    }
    ll query(int a,int b){
        if(a>b)return inf;
        if(s==a && b==e){
            return mini;
        }
        if(b<=m)return l->query(a,b);
        if(m<a)return r->query(a,b);
        return min(l->query(a,m),r->query(m+1,b));
    }
};

int main() {
    //freopen("hi.txt", "r", stdin);
    scanf("%d%d%d",&n,&e,&T);
