    for(int i=1;i<=n;i++){
        scanf("%d",&dis[i]);
    }
    node *wait,*nowait;
    wait=new node(0,n);
    nowait=new node(0,n);
    int waitpos=0;
    for(int x=1;x<=n;x++){
        wait->update(x-1,dp[x-1]-2LL*dis[x]-dis[x-1]+2LL*dis[x]);
        nowait->update(x-1,dp[x-1]-2LL*dis[x]-dis[x-1]);
        while(waitpos<x && T-2*(dis[x]-dis[waitpos+1])<0){
            waitpos++;
        }
        dp[x]=inf;
        ///ll ans=dp[i] +3LL*dis[x]-2LL*dis[i+1]-dis[i];
        ///ans+=max(0LL,(ll)T-2*(dis[x]-dis[i+1]));
        ll ans=nowait->query(0,waitpos-1)+3LL*dis[x];
        dp[x]=min(dp[x],ans);
        ans=wait->query(waitpos,x-1)+3LL*dis[x]+T-2*dis[x];
        dp[x]=min(dp[x],ans);
        //printf("dp %d = %lld\n",x,dp[x]);
    }
    printf("%lld\n",dp[n]+e-dis[n]);
