ll dp[100001];
int N , E , T;
int a[100001];
int opt = 0;
ll cost(int i , int opt)
{
	ll prev = 0;
	if(opt - 1 >= 0)
		prev = dp[opt - 1];
	ll cur = (a[i] - a[opt]) * 2;
	if(T > (a[i] - a[opt]) * 2)
	{
		cur = T + (a[i] - a[opt]);
	}
	return prev + cur;
}
int main()
{
	scanf("%d %d %d" , &N , &E , &T);
