#include <bits/stdc++.h>
using namespace std;

const int N = 100000 + 10;
const long long oo = 0x3f3f3f3f3f3f3f3fll;

struct Node {
	long long flag;
	Node *ls, *rs;
}pool[N*4], *tail=pool, *root[2];

int n, e, t;
long long xx[N];
long long dp[N];

Node *build( int lf, int rg ) {
	Node *nd = ++tail;
	nd->flag = oo;
	if( lf < rg ) {
		int mid = (lf + rg) >> 1;
		nd->ls = build( lf, mid );
		nd->rs = build( mid+1, rg );
	}
	return nd;
}
long long query( Node *nd, int lf, int rg, int pos ) {
	if( lf == rg ) return nd->flag;
	int mid = (lf + rg) >> 1;
	if( pos <= mid )
		return min( query(nd->ls,lf,mid,pos), nd->flag );
	else
		return min( query(nd->rs,mid+1,rg,pos), nd->flag );
}
void modify( Node *nd, int lf, int rg, int L, int R, long long val ) {
	if( L <= lf && rg <= R ) {
		nd->flag = min( nd->flag, val );
		return;
	}
	int mid = (lf + rg) >> 1;
	if( L <= mid ) modify( nd->ls, lf, mid, L, R, val );
	if( R > mid ) modify( nd->rs, mid+1, rg, L, R, val );
}
inline long long cdp( int i ) {
	long long v0 = query(root[0],0,n,i);
	long long v1 = query(root[1],0,n,i);
//	printf( "%d v0 = %lld v1 = %lld\n", i, v0, v1 );
	return min( v0 + xx[i], v1 + xx[i] * 3 );
}
int main() {
	scanf( "%d%d%d", &n, &e, &t );
	for( int i = 1; i <= n; i++ ) 
		scanf( "%lld", xx + i );
	xx[0] = 0;
	root[0] = build( 0, n );
	root[1] = build( 0, n );
	modify( root[0], 0, n, 0, 0, -xx[0] );
	for( int i = 0; i < n; i++ ) {
		dp[i] = cdp(i);
//		printf( "dp[%d] = %lld\n", i, dp[i] );
		int j;
		int lf = i + 1, rg = n;
		while( lf < rg ) {
			int mid = (lf + rg + 1) >> 1;
			if( (xx[mid]<<1) <= (xx[i+1]<<1) + t )
				lf = mid;
			else
				rg = mid - 1;
		}
		j = lf;

		if( i + 1 <= j ) modify( root[0], 0, n, i + 1, j, t - xx[i] + dp[i] );
		if( j + 1 <= n ) modify( root[1], 0, n, j + 1, n, dp[i] - (xx[i+1]<<1) - xx[i] );
	}
	dp[n] = cdp(n);
	long long ans = dp[n] + e - xx[n];
	printf( "%lld\n", ans );
}

