void push_up(int rt)
{
    a[rt] = min(a[rt<<1],a[rt<<1|1]);
    b[rt] = min(b[rt<<1],b[rt<<1|1]);
}
void build(int l,int r,int rt)
{
    a[rt] = b[rt] = INF;
    if(l == r)return;
    int mid = (l+r)>>1;
    build(l,mid,rt<<1);
    build(mid+1,r,rt<<1|1);
}

void update(int l,int r,int rt,int p,ll x,int f)
{
    //cout<<l<<" "<<r<<" "<<p<<" "<<x<<endl;
    if(l == r)
    {
        if(f)a[rt] = min(a[rt],x);
        else b[rt] = min(b[rt],x);
        return;
    }
    int mid = (l+r)>>1;
    if(p<=mid)update(l,mid,rt<<1,p,x,f);
    else update(mid+1,r,rt<<1|1,p,x,f);
    push_up(rt);
}
ll query(int l,int r,int rt,int L,int R,int f)
{
    if(L>R)return INF;
    if(L<=l&&R>=r)return f?a[rt]:b[rt];
    int mid = (l+r)>>1;
    ll ret = INF;
    if(L<=mid)ret = min(ret,query(l,mid,rt<<1,L,min(mid,R),f));
    if(R>mid)ret = min(ret,query(mid+1,r,rt<<1|1,max(L,mid+1),R,f));
    return ret;
}
