		inner_best.insert(get_dyn_minus_unu(i));
		while(2 * (ps[i] - ps[st]) >= t){
			inner_best.erase(inner_best.find(get_dyn_minus_unu(st)));
			best_left = min(best_left, 2 * (ps[i] - ps[st]) + get_dyn_minus_unu(st));
			++st; }
		ll rez = best_left;
		if(!inner_best.empty()){
			rez = min(rez, *begin(inner_best) + t); }
		d[i] = rez; }

	cout << d.back() + e << endl;
	return 0; }

