#include <bits/stdc++.h>

using namespace std;

const int MAXN = 100000 + 10;
const long long INF = (long long)(1e18);

struct IT_min {
    long long T[4 * MAXN];
    int n;

    void init(int _n) {
        n = _n;
        ++n;
        for(int i = 0; i <= 4 * n; ++i) T[i] = INF;
    }

    void update(int x, int i, int j, int p, long long v) {
        if ((j < p) || (i > p)) return;
        if (i == j) {
            T[x] = v;
            return;
        }
        int mid = (i + j) >> 1;
        update(x + x, i, mid, p, v);
        update(x + x + 1, mid + 1, j, p, v);
        T[x] = min(T[x + x], T[x + x +1]);
    }

    long long get_min(int x, int i, int j, int l, int r) {
        if ((j < l) || (i > r)) return INF;
        if ((i >= l) && (j <= r)) return T[x];
        int mid = (i + j) >> 1;
        return min(get_min(x + x, i, mid, l, r), get_min(x + x + 1, mid + 1, j, l, r));
    }

    long long get_min(int l, int r) {
        ++l; ++r;
        if (r < 1) return 0;
        if (l > r) return 0;
        return get_min(1, 1, n, l, r);
    }

    void update(int p, long long v) {
        ++p;
        update(1, 1, n, p, v);
    }

} T1, T2;

int n, e;
long long t;
long long x[MAXN];
long long f[MAXN];

void run() {
    cin >> n >> e >> t;
    for(int i = 1; i <= n; i++) cin >> x[i];

    x[0] = 0;
    f[0] = 0;

    T1.init(n); T2.init(n);
    T1.update(0, f[0] + -x[0]); T2.update(0, f[0] + -x[1] * 2 - x[0]);
    for(int i = 1; i <= n; ++i) {
        int j = upper_bound(x, x + n + 1, x[i] - (t / 2) - (t % 2)) - x;
        f[i] = INF;
        if (j <= i) {
            f[i] = x[i] + T1.get_min(j - 1, i - 1) + t;
            //cout << ">> " << j << " " << x[i] << " " << f[i] << endl;
        }
        j = min(j - 1, i);
        if (j > 0) {
            f[i] = min(f[i], x[i] * 3 + T2.get_min(0, j - 1));
        }
        T1.update(i, f[i] + -x[i]);
        T2.update(i, f[i] + -x[i + 1] * 2 - x[i]);
        //cout << i << " " << f[i] << endl;
    }

    cout << f[n] + (e - x[n]) << endl;
}

