ll solve(int x) {
	if (x == n) return 0;
	int lo = x + 1, hi = n, mid;

	while (lo != hi) {
		mid = (lo + hi) / 2;
		if (3 * (p[mid] - p[x]) <= 2 * t + p[mid] - p[x]) lo = mid + 1;
		else hi = mid;
	}

	ll ret = 0;
	if (lo == x + 1) ret = t;
	else ret = 3 * (p[lo - 1] - p[x]);

	ret += p[lo] - p[lo - 1];

	return ret + solve(lo);
}

