template<typename Monoid>
struct SegmentTree{
    typedef function<Monoid(Monoid,Monoid)> F;
    int n;
    F f;
    Monoid id;
    vector<Monoid> dat;
    SegmentTree(int n_,F f,Monoid id):f(f),id(id){init(n_);}
    void init(int n_){
        n=1;
        while(n<n_) n<<=1;
        dat.assign(n<<1,id);
    }
    void build(const vector<Monoid> &v){
        for (int i=0;i<v.size();++i) dat[i+n]=v[i];
        for (int i=n-1;i;--i) dat[i]=f(dat[i<<1|0],dat[i<<1|1]);
    }
    void update(int k,Monoid x){
        dat[k+=n]=x;
        while(k>>=1) dat[k]=f(dat[k<<1|0],dat[k<<1|1]);
    }
    Monoid query(int a,int b){
        if (a>=b) return id;
        Monoid vl=id,vr=id;
        for (int l=a+n,r=b+n;l<r;l>>=1,r>>=1){
            if (l&1) vl=f(vl,dat[l++]);
            if (r&1) vr=f(dat[--r],vr);
        }
        return f(vl,vr);
    }
    Monoid operator[](int i){
        return dat[i+n];
    }
    // most left position that meets condition "check"
    template<typename C>
    int find(int a,int b,const C &check,int k,int l,int r){
        if (!check(dat[k])||r<=a||b<=l) return -1;
        if (l+1==r) return k-n;
        int vl=find(a,b,check,k<<1|0,l,(l+r)>>1);
        if (~vl) return vl;
        return find(a,b,check,k<<1|1,(l+r)>>1,r);
    }
    template<typename C>
    int find(int a,int b,const C &check){
        return find(a,b,check,1,0,n);
    }
};

int main(){
    cin.tie(0);
    ios::sync_with_stdio(false);
    int N; ll E,T; cin >> N >> E >> T;
    vector<ll> x(N+1); x[0]=0;
    for (int i=1;i<=N;++i) cin >> x[i];
    vector<ll> x_2(N+1);
    for (int i=0;i<=N;++i) x_2[i]=x[i]*2;
    vector<ll> dp(N+1,INF),dp0(N+1);
    SegmentTree<ll> seg(N+1,[](ll a,ll b){return min(a,b);},INF);
    dp[0]=0; dp0[0]=-x_2[1];
    seg.update(0,0);
    for (int i=1;i<=N;++i){
        int pos=upper_bound(x_2.begin(),x_2.end(),x_2[i]-T)-x_2.begin()-2;
        if (pos>=0) dp[i]=min(dp[i],dp0[pos]+3*x[i]);
        dp[i]=min(dp[i],seg.query(max(0,pos+1),i)+T+x[i]);
        if (i==N) continue;
        dp0[i]=min(dp0[i-1],dp[i]-x_2[i+1]-x[i]);
        seg.update(i,dp[i]-x[i]);
    }
    cout << dp[N]+(E-x[N]) << '\n';
}

