inline void segAssign(int id, ll val) {
  for (seg[id += n] = val; id > 1; id >>= 1)
    seg[id >> 1] = min(seg[id], seg[id ^ 1]);
}

inline ll segGet(int l, int r) {
  ll res = 1e18;
  for (l += n, r += n; l < r; l >>= 1, r >>= 1) {
    if (l & 1) smin(res, seg[l++]);
    if (r & 1) smin(res, seg[--r]);
  }
  return res;
}

