const int N = 1e5 + 10;
const int BINF = 1e18 + 10;
int dp[N], a[N], t[4 * N][2];

void update(int v, int l, int r, int need, int vl1, int vl2) {
	if (l == r) {
		t[v][0] = vl1;
		t[v][1] = vl2;
		return;
	}
	int mid = (l + r) / 2;
	if (need <= mid) {
		update(2 * v, l, mid, need, vl1, vl2);
	}
	else {
		update(2 * v + 1, mid + 1, r, need, vl1, vl2);
	}
	t[v][0] = min(t[2 * v][0], t[2 * v + 1][0]);
	t[v][1] = min(t[2 * v][1], t[2 * v + 1][1]);
}

int get(int v, int l, int r, int a, int b, int id) {
	if (l > b || r < a) {
		return BINF;
	}
	if (l >= a && r <= b) {
		return t[v][id];
	}
	int mid = (l + r) / 2;
	return min(get(2 * v, l,  mid, a, b, id), get(2 * v + 1, mid + 1, r, a, b, id));
}

