		cin >> a[i];
	}
	dp[1] = a[1] + t;
	update(1, 1, n, 1, 0, -2ll * a[1]);
	int ptr = 0;
	for (int i = 2; i <= n; i++) {
		while (2ll * (a[i] - a[ptr + 1]) >= t) {
			ptr++;
		}
		//cout << i << ' ' << ptr << '\n';
		dp[i] = BINF;
		for (int j = i; j <= i; j++) {
			dp[i] = min(dp[i], dp[j - 1] + (a[i] - a[j - 1]) + max(2 * (a[i] - a[j]), t));
		}
		dp[i] = min(dp[i], get(1, 1, n, 1, ptr, 1) + 3ll * a[i]);
		dp[i] = min(dp[i], get(1, 1, n, ptr + 1, i - 1, 0) + t + a[i]);
		update(1, 1, n, i, dp[i - 1] - a[i - 1], dp[i - 1] - a[i - 1] - 2ll * a[i]);
		//cout << dp[i] << '\n';
	}
	//cout << '\n';
	cout << dp[n] + (e - a[n]) << '\n';
}

