const int N=101000;
int n,e,t;
ll dp[N],x[N];
struct node {
	ll s,fg;
}nd[4*N];
void upd(int p) {
	nd[p].s=min(nd[p+p].s,nd[p+p+1].s);
}
void setf(int p,ll v) {
	nd[p].fg+=v; nd[p].s+=v;
}
void build(int p,int l,int r) {
	nd[p].fg=0;
	if (l==r) {
	} else {
		int md=(l+r)>>1;
		build(p+p,l,md);
		build(p+p+1,md+1,r);
		upd(p);
	}
}
void push(int p) {
	if (nd[p].fg) {
		setf(p+p,nd[p].fg);
		setf(p+p+1,nd[p].fg);
		nd[p].fg=0;
	}
}
ll query(int p,int l,int r,int tl,int tr) {
	if (tl==l&&tr==r) return nd[p].s;
	else {
		push(p);
		int md=(l+r)>>1;
		if (tr<=md) return query(p+p,l,md,tl,tr);
		else if (tl>md) return query(p+p+1,md+1,r,tl,tr);
		else return min(query(p+p,l,md,tl,md),query(p+p+1,md+1,r,md+1,tr));
	}
}
void modify(int p,int l,int r,int tl,int tr,ll v) {
	if (tl>tr) return;
	if (tl==l&&tr==r) return setf(p,v);
	else {
		push(p);
		int md=(l+r)>>1;
		if (tr<=md) modify(p+p,l,md,tl,tr,v);
		else if (tl>md) modify(p+p+1,md+1,r,tl,tr,v);
		else modify(p+p,l,md,tl,md,v),modify(p+p+1,md+1,r,md+1,tr,v);
		upd(p);
	}
}

int main() {
	scanf("%d%d%d",&n,&e,&t);
	rep(i,1,n+1) scanf("%lld",x+i);
	int r=0;
	modify(1,0,n,0,0,t);
	rep(i,1,n+1) {
		dp[i]=1ll<<60;
		modify(1,0,n,0,r-1,3*(x[i]-x[i-1]));
		modify(1,0,n,r,i-1,x[i]-x[i-1]);
		while (2*(x[i]-x[r+1])>t) {
			modify(1,0,n,r,r,(2*(x[i]-x[r+1]))-t);
			r++;
		}
		dp[i]=query(1,0,n,0,i-1);
		modify(1,0,n,i,i,dp[i]+t);
	}
	printf("%lld\n",dp[n]+e-x[n]);
}

