typedef long long ll;

int N;

ll q (ll* x, int i) {
        ll res = 1e18;
        for (i += N+5; i; i /= 2) res = min(res, x[i]);
        return res;
}

void u (ll* x, int l, int r, ll mn) {
        for (l += N+5, r += N+5; l<r; l /= 2, r /= 2) {
                if (l&1) x[l] = min(x[l], mn), l++;
                if (r&1) --r, x[r] = min(x[r], mn);
        }
}

ll E, T, x[100100], dp[100100], close[200200], far[200200];

int main () {
        scanf("%d %lld %lld", &N, &E, &T);
        fo(i, 0, N) scanf("%lld", &x[i]);
        x[N] = E;

        fo(i, 0, 200200) close[i] = far[i] = 1e18;

        dp[0] = x[0];
        fo(i, 0, N) {
                int lo = i, hi = N-1, mid, v = -1;
                while (lo <= hi) {
                        mid = (lo+hi)/2;
                        if (2*(x[mid]-x[i]) <= T) lo = mid+1, v = mid;
                        else hi = mid-1;
                }

                u(close, i+1, v+2, dp[i] + T - x[i]);
                u(far, v+2, N+1, dp[i] - 3*x[i]);

                dp[i+1] = min(q(close, i+1) + x[i], q(far, i+1) + 3*x[i]);
                dp[i+1] += x[i+1] - x[i];
        }

        printf("%lld\n", dp[N]);
        return 0;
}

