		modify(tree1,1,1,N,i,f[i-1]+(ll)X[i]-3LL*(ll)X[i]-(ll)X[i-1]);
		int l = 0,r = i-1,mid; f[i] = inf;
		while (l <= r)
		{
			mid = (l+r)>>1;
			if (T-2LL*(X[i]-X[mid+1]) <= 0) l = mid+1;
			else r = mid-1;
		}
		if (r+1) f[i] = query(tree1,1,1,N,1,r+1)+3LL*X[i];
		for (int j = r+1;j <= r+15&&j < i;++j)
			f[i] = min(f[j]+(ll)X[j+1]-3LL*(ll)X[j+1]-(ll)X[j]+3LL*(ll)X[i]+(ll)(i-j)*(ll)((ll)T-2LL*(X[i]-X[j+1])),f[i]);
	}
	cout << f[N]+(ll)E-(ll)X[N] << endl;
	return 0;
}

