#define int long long

const long long INF = 1e18;
const int MAX_N = 100 * 1000 + 5;
int n, e, t, a[MAX_N];
long long dp[MAX_N], seg[2][4 * MAX_N];

void read_input() {
	cin >> n >> e >> t;
	for (int i = 1; i <= n; i++)
		cin >> a[i];
}

void modify(int w, int p, long long x, int id = 1, int l = 0, int r = n + 1) {
	if (l == r - 1) {
		seg[w][id] = min(seg[w][id], x);
		return;
	}
	int mid = (l + r) / 2;
	if (p < mid)
		modify(w, p, x, id * 2, l, mid);
	else
		modify(w, p, x, id*2+1, mid, r);
	seg[w][id] = min(seg[w][id * 2], seg[w][id*2+1]);
}

long long get(int w, int x, int y, int id = 1, int l = 0, int r = n + 1) {
	if (x >= r || y <= l)
		return INF;
	if (x <= l && y >= r)
		return seg[w][id];
	int mid = (l + r) / 2;
	return min(get(w, x, y, id * 2, l, mid), get(w, x, y, id*2+1, mid, r));
}

void solve() {
	memset(seg, 63, sizeof seg);
	modify(0, 0, 0);
	modify(1, 0, -2 * a[1]);
	for (int i = 1; i <= n; i++) {
		int p = lower_bound(a, a + n + 1, a[i] - t / 2) - a - 1;
//		cerr << "* " << i << " : " << p << endl;
		dp[i] = min(get(0, p, n + 1) + a[i] + t, get(1, 0, p) + 3 * a[i]);
		modify(0, i, dp[i] - a[i]);
		modify(1, i, dp[i] - 2 * a[i + 1] - a[i]);
	}
}

void print_output() {
//	for (int i = 1; i <= n; i++)
//		cerr << i << " : " << dp[i] << endl;
	cout << dp[n] + e - a[n];
}

int32_t main() {
	ios::sync_with_stdio(false), cin.tie(0), cout.tie(0);
	read_input();
	solve();
	print_output();
}

