  cin >> n >> e >> t;
  assert (n < N);
  VL x(n);
  SegTree<ll, max_fun> raw(n + 1, max_fun(), inf); // dp[i]
  SegTree<ll, max_fun> boiled(n + 1, max_fun(), inf); // dp[i] - 2 * x[i]
  REP(i, 0, n) {
    cin >> x[i];
  }
  dp[0] = 0;
  raw.update(0, 0);
  boiled.update(0, -2 * x[0]);
  REP(i, 0, n) {
    // Compute the range of j s.t. t <= 2 * (x[i] - x[j]) (0 <= j <= i)
    int j_boundary =
      upper_bound(x.begin(), x.begin() + i + 1, x[i] - (t+1)/2) - x.begin();
    ll ret = inf;
    ret = min(ret, boiled.query(0, j_boundary - 1) + 2 * x[i]);
    ret = min(ret, raw.query(j_boundary, i) + t);
    dp[i + 1] = ret;
    raw.update(i + 1, ret);
    if (i < n - 1) {
      boiled.update(i + 1, ret - 2 * x[i + 1]);
    }
  }
  cout << dp[n] + e << endl; 
