template <class T> class ST{
	T x[ST_MAX * 2];

	T eval(T a , T b){
		return min(a , b);
	}

	void ren(int a){
		x[a] = eval(x[2 * a] , x[2 * a + 1]);
		if(a > 0) ren(a / 2);
	}

	T read_ST(int a , int b , int e , int s , int f){
		if(e <= s || f <= b) return INF;
		if(s <= b && e <= f) return x[a];
		return eval(read_ST(2 * a , b , (b + e) / 2 , s , f) , read_ST(2 * a + 1 , (b + e) / 2 , e , s , f));
	}

public:

	ST(){
		fill(x , x + 2 * ST_MAX , INF);
	}

	void put(int a , T b){
		x[a + ST_MAX] = b;
		ren((a + ST_MAX) / 2);
	}

	T get(int s , int f){
		return read_ST(1 , 0 , ST_MAX , s , f);
	}
};

