			for (int ii = i; ii <= j; ii++)
				used[ii] = true;
			tt += std::max(t, (v[j] - v[i]) * 2);
			tt += v[i + 1] - v[i];
		}
	}
	if (used[n - 1] == false) {
		tt += t;
	}
	tt += e - v[n - 1];
	std::cout << tt << std::endl;
}

