template<typename T>
struct Segtree {
    int n;
    T e;
    vector<T> dat;
    typedef function<T(T a, T b)> Func;
    Func f;

    Segtree(){}
    Segtree(int n_input, Func f_input, T e_input){
        initialize(n_input, f_input, e_input);
    }
    void initialize(int n_input, Func f_input, T e_input){
        f = f_input;
        e = e_input;
        n = 1;
        while(n < n_input) n <<= 1;
        dat.resize(2*n-1, e);
    }

    void update(int k, T a){
        k += n - 1;
        dat[k] = a;
        while(k > 0){
            k = (k - 1)/2;
            dat[k] = f(dat[2*k+1], dat[2*k+2]);
        }
    }

    T get(int k){
        return dat[k+n-1];
    }

    T between(int a, int b){
        return query(a, b+1, 0, 0, n);
    }

    T query(int a, int b, int k, int l, int r){
        if(r<=a || b<=l) return e;
        if(a<=l && r<=b) return dat[k];
        T vl = query(a, b, 2*k+1, l, (l+r)/2);
        T vr = query(a, b, 2*k+2, (l+r)/2, r);
        return f(vl, vr);
    }
};

void chmin(int64_t& a, int64_t b){
    a = min(a, b);
}

int main(){
    int N, E, T, X[100001];
    cin >> N >> E >> T;
    X[0] = 0;
    for(int i=1; i<=N; i++) cin >> X[i];

    const int64_t INF = 1e18;
    Segtree<int64_t> dp(N+1, [](int64_t a, int64_t b){ return min(a, b);}, INF);
    Segtree<int64_t> sub(N+1, [](int64_t a, int64_t b){ return min(a, b);}, INF);
    dp.update(0, 0);
    sub.update(0, -2*X[1]);

    int pt = 0;
    for(int i=1; i<=N; i++){
        while(T < 2*(X[i] - X[pt+1])) pt++;
        int64_t result = INF;
        if(pt < i) chmin(result, dp.between(pt, i-1) + T);
        if(pt > 0) chmin(result, sub.between(0, pt-1) + 2*X[i]);
        dp.update(i, result);
        if(i < N) sub.update(i, result - 2*X[i+1]);
    }
    int64_t ans = dp.get(N) + E;
    cout << ans << endl;
    return 0;
}

