        v.push_back(now_max); now_max--;
    }
    v.push_back(now_min); now_min++;
    sol(n / 2, now_min, now_max, v);
}
template< typename Monoid >
struct SegmentTree {
    using F = function< Monoid(Monoid, Monoid) >;

    int sz;
    vector< Monoid > seg;

    const F f;
    const Monoid M1;

    SegmentTree(int n, const F f, const Monoid& M1) : f(f), M1(M1) {
        sz = 1;
        while (sz < n) sz <<= 1;
        seg.assign(2 * sz, M1);
    }

    void set(int k, const Monoid& x) {
        seg[k + sz] = x;
    }

    void build() {
        for (int k = sz - 1; k > 0; k--) {
            seg[k] = f(seg[2 * k + 0], seg[2 * k + 1]);
        }
    }

    void update(int k, const Monoid& x) {
        k += sz;
        seg[k] = x;
        while (k >>= 1) {
            seg[k] = f(seg[2 * k + 0], seg[2 * k + 1]);
        }
    }

    Monoid query(int a, int b) {
        Monoid L = M1, R = M1;
        for (a += sz, b += sz; a < b; a >>= 1, b >>= 1) {
            if (a & 1) L = f(L, seg[a++]);
            if (b & 1) R = f(seg[--b], R);
        }
        return f(L, R);
    }

    Monoid operator[](const int& k) const {
        return seg[k + sz];
    }

    template< typename C >
    int find_subtree(int a, const C& check, Monoid& M, bool type) {
        while (a < sz) {
            Monoid nxt = type ? f(seg[2 * a + type], M) : f(M, seg[2 * a + type]);
            if (check(nxt)) a = 2 * a + type;
            else M = nxt, a = 2 * a + 1 - type;
        }
        return a - sz;
    }


    template< typename C >
    int find_first(int a, const C& check) {
        Monoid L = M1;
        if (a <= 0) {
            if (check(f(L, seg[1]))) return find_subtree(1, check, L, false);
            return -1;
        }
        int b = sz;
        for (a += sz, b += sz; a < b; a >>= 1, b >>= 1) {
            if (a & 1) {
                Monoid nxt = f(L, seg[a]);
                if (check(nxt)) return find_subtree(a, check, L, false);
                L = nxt;
                ++a;
            }
        }
        return -1;
    }

    template< typename C >
    int find_last(int b, const C& check) {
        Monoid R = M1;
        if (b >= sz) {
            if (check(f(seg[1], R))) return find_subtree(1, check, R, true);
            return -1;
        }
        int a = sz;
        for (b += sz; a < b; a >>= 1, b >>= 1) {
            if (b & 1) {
                Monoid nxt = f(seg[--b], R);
                if (check(nxt)) return find_subtree(b, check, R, true);
                R = nxt;
            }
        }
        return -1;
    }
};
signed main() {
    int n,e,t; cin >> n>>e>>t;
    SegmentTree< int > seg(n+1, [](int a, int b) { return min(a, b); }, INT_MAX);
    SegmentTree< int > seg2(n+1, [](int a, int b) { return min(a, b); }, INT_MAX);
    vel x(n + 1, 0);
    rep(i, n) { cin >> x[i+1]; }
    seg.update(0,0);
    seg2.update(0, -2 * x[1]);
    int ret;
    for(int i=1;i<=n;i++) {
        int l = lower_bound(x.begin(), x.end(), x[i] - t/2) - x.begin();
        if (l > 0) { l--; }
        int ans1 = x[i] + seg.query(l, i)+t;
        mmin(ans1, 3 * x[i]+ seg2.query(0, l));
        if (i == n) { ret = ans1 + e - x[i]; }
        else {
            seg.update(i, ans1 - x[i]);
            seg2.update(i, ans1 - x[i] - 2 * x[i + 1]);
        }
    }
    cout << ret << endl;
    return 0;
}

