  V<lint> x(n); for (auto&& e : x) cin >> e;
  x.push_back(E);
  V<lint> dp(n + 1, 9e18);
  dp[n] = 0;
  for (int i = n - 1; i >= 0; --i) {
    int k = lower_bound(begin(x), end(x), x[i] + (T + 1) / 2) - begin(x) + 1;
