      if (j < k) {
        dp[i] = min(dp[i], T - x[i] + x[j] + dp[j]);
      } else {
        dp[i] = min(dp[i], -3 * x[i] + 2 * x[j - 1] + x[j] + dp[j]);
      }
    }
  }
  cout << x[0] + dp[0] << '\n';
}

