vi seg,seg2;
ll N;
void init(vi v){
    N=1;
    while(N<v.size())N*=2;
    seg=vi(N*2-1,inf);
    seg2=seg;
}
void update(ll i,ll x){
    i=N+i-1;
    seg[i]=x;
    while(i>0){
        i=(i-1)/2;
        seg[i]=min(seg[i*2+1],seg[i*2+2]);
    }
}
ll getmi(ll a,ll b,ll k,ll l,ll r){
    if(r<=a||b<=l)return inf;
    if(a<=l&&r<=b)return seg[k];
    ll c1=getmi(a,b,k*2+1,l,(l+r)/2);
    ll c2=getmi(a,b,k*2+2,(l+r)/2,r);
    return min(c1,c2);
}
void update2(ll i,ll x){
    i=N+i-1;
    seg2[i]=x;
    while(i>0){
        i=(i-1)/2;
        seg2[i]=min(seg2[i*2+1],seg2[i*2+2]);
    }
}
ll getmi2(ll a,ll b,ll k,ll l,ll r){
    if(r<=a||b<=l)return inf;
    if(a<=l&&r<=b)return seg2[k];
    ll c1=getmi2(a,b,k*2+1,l,(l+r)/2);
    ll c2=getmi2(a,b,k*2+2,(l+r)/2,r);
    return min(c1,c2);
}
int main(){
    ll n,e,t;cin>>n>>e>>t;
    vi dis(n+1);
    rep(i,n)cin>>dis[i+1];
    vi dp(n+1,inf);
    dp[0]=0;
    init(dp);
    update(0,0);
    update2(0,-dis[1]*2);
    REP(i,1,n+1){
        ll f=lb(dis,dis[i]-t/2);
        if(!f)f++;
        chmin(dp[i],getmi(f-1,i,0,0,N)+dis[i]+t);
        chmin(dp[i],getmi2(0,f-1,0,0,N)+dis[i]*3);
        update(i,dp[i]-dis[i]);
        if(i<n)update2(i,dp[i]-dis[i]-dis[i+1]*2);
    }
    out(dp[n]+e-dis[n]);
}

