int inf = 1e16;

vector<int> tree;
int N = 1;

void upd(int v) {
	if(v == 0) 
		return;
	tree[v] = max(tree[v * 2], tree[v * 2 + 1]);
	upd(v / 2);
}

int get(int v, int l, int r, int vl, int vr) {
	if(vr <= l || r <= vl)
		return inf;
	if(vl <= l && r <= vr) {
		return tree[v];
	}
	int m = (l + r) / 2;
	return min(get(v * 2, l, m, vl, vr), get(v * 2 + 1, m, r, vl, vr));
}

signed main() {
	#ifdef PC
		freopen("in.txt", "r", stdin);
		freopen("out.txt", "w", stdout);
	#endif	

	int n, k, e;
	cin >> n >> e >> k;
	vector<int> a(n);
	for (int i = 0; i < n; i++) 
		cin >> a[i];

	while(N < (n + 2)) 
		N *= 2;

	tree = vector<int> (2 * N, inf);
	vector<int> suf(n + 1, inf);
	suf[n - 1] = k;
	int uk = n - 2;
	tree[N + (n - 2)] = k + a[n - 1] + suf[n - 1];
	upd((N + (n - 2)) / 2);

	for (int i = n - 2; i >= 0; i--) {

		while(uk > 0 && 2 * (a[uk] - a[i]) >= k) {
			tree[N + uk] = 2 * a[uk] + a[uk + 1] + suf[uk + 1];
			upd((N + (uk - 1)) / 2);
			uk--;
		}
		int ans = max(k, 2 * (a[n - 1] - a[i])) + (a[n - 1] - a[i]);
		int l = i, r = n + 1;
		while(r - l > 1) {
			int m = (l + r) / 2;
			if(2 * (a[m] - a[i]) < k) 
				l = m;
			else
				r = m;
		}
		// cout << ans << " " << i << " " << l << " " << get(1, 0, N, i, l + 1) << " " << uk << endl;
		ans = min(ans, get(1, 0, N, i, l + 1) - a[i]);
		if(r <= n - 2) {
			ans = min(ans, get(1, 0, N, l + 1, r + 1) - 3 * a[i]);
		}
		suf[i] = ans;
		if(i != 0) {
			tree[N + (i - 1)] = k + a[i] + suf[i];
			upd((N + (i - 1)) / 2);
		}
		// cout << i << " " << suf[i] << endl;
	}
	cout << a[0] + suf[0] + (e - a[n - 1]) << endl;
	return 0;
}

