    rep (i, n) {
        if (dp[i] == INF) continue;
        ll nx;
        if (t % 2 == 0) nx = x[i + 1] + t / 2;
        else nx = x[i + 1] + (t + 1) / 2;

        auto itr = lower_bound(all(x), nx);
        int ni = itr - x.begin();
        if (ni <= n) {
            chmin(dp[ni], dp[i] + (x[i + 1] - x[i]) + (x[ni] - x[i + 1]) * 3);
        }

        ni--;
