        if (ni + 1 < x.size()) {
            assert((x[ni + 1] - x[i + 1]) * 2 >= t);
        }
        if (ni > i) {
            chmin(dp[ni], dp[i] + (x[i + 1] - x[i]) + t + (x[ni] - x[i + 1]));
        }
    }
    cout << dp[n] + e - x[n] << endl;
