template<typename T> bool chmax(T& a,const T& b){return a < b ? (a = b,true) : false;}
template<typename T> bool chmin(T& a,const T& b){return b < a ? (a = b,true) : false;}
typedef long long ll;

ll N,E,T;
const ll MAX_N = 131072;
ll dp [MAX_N];
pair<ll,ll> seg_tree [MAX_N * 2];
const ll INF = 1e17;

void update(int idx,pair<ll,ll> val)
{
	idx += MAX_N - 1;
	seg_tree [idx] = val;
	while(idx){
		idx = (idx - 1) / 2;
		seg_tree [idx] = min(seg_tree [idx * 2 + 1],seg_tree [idx * 2 + 2]);
	}
}

void init()
{
	FOR(i,0,MAX_N){
		update(i,{INF,-1});
	}
}

pair<ll,ll> query(int a,int b,int idx,int l,int r)
{
	if(b <= l || r <= a) return {INF,-1};
	if(a <= l && r <= b) return seg_tree [idx];
	pair<ll,ll> res = query(a,b,idx * 2 + 1,l,(l + r) / 2);
	chmin(res,query(a,b,idx * 2 + 2,(l + r) / 2,r));
	return res;
}

int main()
{
	cin.tie(nullptr);
	ios::sync_with_stdio(false);

	cin >> N >> E >> T;
	vector<ll> A(N);
	FOR(i,0,N){
		cin >> A [i];
	}

	init();
	fill_n(dp,N + 1,INF);
	dp [0] = 0;
	update(0,make_pair(-A [0],0));
	for(int i = 1;i <= N;i++){
		pair<ll,ll> nxt = query(0,i,0,0,MAX_N);
		dp [i] = dp [nxt.second];
		ll a = A [i - 1] - (nxt.second > 0 ? A [nxt.second - 1] : 0);
		ll b = A [nxt.second] - (nxt.second > 0 ? A [nxt.second - 1] : 0);
		dp [i] += max(a * 2 - b * 2,T) + a;
		if(i < N){
			update(i,{dp [i] - (A [i - 1] + A [i]),i});
		}
	}

	cout << dp [N] + (E - A.back()) << endl;

	return 0;
}

