void update(int x, int val, int b = 0, int e = n, int id = 1) {
    if (e - b == 1) {
        seg[id].X = val;
        seg[id].Y = x;
     //   cout << id << ">>>>>>>>>" << val << "   ";
        return;
    }
    int mid = e + b >> 1;
    if (x >= mid)
        update(x, val, mid, e, id << 1 | 1);
    else
        update(x, val, b, mid, id << 1);
    seg[id] = Merge(seg[id << 1 | 1], seg[id << 1]);
   // cout << endl;
   // cout << (id << 1)<< " BBBBBBB " << seg[id << 1].X << endl;
}
pii get(int l, int r, int b = 0, int e = n, int id = 1) {
 //   cout << "A";
